<template>
  <div class="websocket">
    <a-button @click="send">发消息</a-button>
    <br><br>
    <a-image
      :width="300"
      :height="400"
      :src="'data:image/png;base64,'+ imageBase64"
      fallback="data:image/jpg;base64,"
    />
  </div>
</template>

<script>

export default {
  name: 'Websocket',
  data () {
    return {
        path:"ws://192.168.190.70:8009/wsserver/123",
        // path:"ws://127.0.0.1:8005/wsserver/123",
        socket:"",
        imageBase64: ""
    }
  },
  mounted () {
    // 初始化
    this.init()
  },
  methods: {
    init: function () {
      if(typeof(WebSocket) === "undefined"){
          alert("您的浏览器不支持socket")
      } else {
          // 实例化socket
          this.socket = new WebSocket(this.path)
          // 监听socket连接
          this.socket.onopen = this.open
          // 监听socket错误信息
          this.socket.onerror = this.error
          // 监听socket消息
          this.socket.onmessage = this.getMessage
      }
    },
    open: function () {
        console.log("socket连接成功")
    },
    error: function () {
        console.log("连接错误")
    },
    getMessage: function (msg) {
      console.log(msg.data)
      // this.imageBase64 = msg.data
      let obj = JSON.parse(msg.data);
      if (obj.code == 500) {
        this.$message.error(obj.message);
      }
      
    },
    send: function () {
      let data = {
        "fromUserId": 1,
        "toUserId": 2,
        "streamId": 22
      };
      this.socket.send(JSON.stringify(data))
    },
    close: function () {
        console.log("socket已经关闭")
    }
  },
  unmounted () {
    // 销毁监听
    this.socket.onclose = this.close
  }
}
</script>
